INSERT INTO review (id, first_name, last_name, review)
VALUES (1000, 'Daniel', 'Styszko', 'Pizza była wspaniała, cienkie i chrupiące ciasto, obfite dodatki, świeże składniki, doskonałe połączenie smaków. Polecam wszystkim, którzy szukają wyjątkowej pizzy.'),
       (1001, 'Andrzej', 'Nowak', 'Dostawa pizzy była szybka, a obsługa miła. Polecam!'),
       (1002, 'Marta', 'Jankowska', 'Pizza była wyśmienita - cienkie i chrupiące ciasto w połączeniu z obfitością smacznych dodatków. Dostawa była błyskawiczna, a pizza dotarła ciepła i gotowa do jedzenia. Obsługa była miła i uprzejma, sprawiając, że całe doświadczenie było przyjemne. Gorąco polecam tę pizzę każdemu, kto szuka doskonałego smaku, szybkiej dostawy i miłej obsługi.'),
       (1003, 'Piotr','Kowalczyk', 'Pizza, którą zamówiłem, była naprawdę smaczna. Ciasto miało idealną chrupkość, a dodatki były obfite i aromatyczne. Dostawa była bardzo szybka, pizza dotarła jeszcze gorąca. Obsługa była przyjazna i profesjonalna, co dodatkowo umiliło mi to doświadczenie. Całościowo, byłem bardzo zadowolony z tej pizzy i z pewnością polecam ją innym miłośnikom pysznego jedzenia.'),
       (1004, 'Kamil', 'Wiśniewski', 'byłem bardzo zadowolony z jej jakości. Smakowała wyśmienicie, a dodatki były doskonale zbalansowane. Dostawa była szybka i sprawnie zorganizowana, co było dla mnie ważne. Obsługa była miła i uprzejma, sprawiając, że całe doświadczenie było przyjemne. Zdecydowanie polecam tę pizzę każdemu, kto szuka smacznego posiłku i profesjonalnej obsługi.');